import React, { useState } from 'react';
import { ShoppingCart, Plus, Minus, Star, MapPin, Phone, Clock } from 'lucide-react';

const RestaurantApp = () => {
  const [activeTab, setActiveTab] = useState('menu');
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);

  const menuItems = [
    {
      id: 1,
      name: 'Nasi Gudeg Yogya',
      price: 25000,
      category: 'Makanan Utama',
      image: 'https://images.unsplash.com/photo-1596040033229-a9821ebd058d?w=300&h=200&fit=crop',
      rating: 4.8,
      description: 'Gudeg khas Yogyakarta dengan ayam kampung dan telur'
    },
    {
      id: 2,
      name: 'Soto Ayam Lamongan',
      price: 20000,
      category: 'Makanan Utama',
      image: 'https://images.unsplash.com/photo-1565299624946-b28f40a0ca4b?w=300&h=200&fit=crop',
      rating: 4.7,
      description: 'Soto ayam dengan kuah bening dan bumbu rempah pilihan'
    },
    {
      id: 3,
      name: 'Rendang Daging',
      price: 35000,
      category: 'Makanan Utama',
      image: 'https://images.unsplash.com/photo-1563379091339-03246963d96c?w=300&h=200&fit=crop',
      rating: 4.9,
      description: 'Rendang daging sapi dengan bumbu tradisional Padang'
    },
    {
      id: 4,
      name: 'Gado-Gado Jakarta',
      price: 18000,
      category: 'Makanan Utama',
      image: 'https://images.unsplash.com/photo-1512058564366-18510be2db19?w=300&h=200&fit=crop',
      rating: 4.5,
      description: 'Gado-gado dengan sayuran segar dan bumbu kacang'
    },
    {
      id: 5,
      name: 'Es Teh Manis',
      price: 8000,
      category: 'Minuman',
      image: 'https://images.unsplash.com/photo-1556679343-c7306c1976bc?w=300&h=200&fit=crop',
      rating: 4.3,
      description: 'Es teh manis segar untuk menemani makan'
    },
    {
      id: 6,
      name: 'Jus Alpukat',
      price: 15000,
      category: 'Minuman',
      image: 'https://images.unsplash.com/photo-1623065422902-30a2d299bbe4?w=300&h=200&fit=crop',
      rating: 4.6,
      description: 'Jus alpukat segar dengan susu kental manis'
    },
    {
      id: 7,
      name: 'Pisang Goreng',
      price: 12000,
      category: 'Dessert',
      image: 'https://images.unsplash.com/photo-1587132164002-f4f4c1b2c64c?w=300&h=200&fit=crop',
      rating: 4.4,
      description: 'Pisang goreng krispy dengan topping cokelat'
    },
    {
      id: 8,
      name: 'Es Cendol',
      price: 10000,
      category: 'Dessert',
      image: 'https://images.unsplash.com/photo-1563805042-7684c019e1cb?w=300&h=200&fit=crop',
      rating: 4.5,
      description: 'Es cendol dengan santan dan gula merah'
    }
  ];

  const categories = ['Semua', 'Makanan Utama', 'Minuman', 'Dessert'];
  const [selectedCategory, setSelectedCategory] = useState('Semua');

  const filteredMenu = selectedCategory === 'Semua' 
    ? menuItems 
    : menuItems.filter(item => item.category === selectedCategory);

  const addToCart = (item) => {
    const existingItem = cart.find(cartItem => cartItem.id === item.id);
    if (existingItem) {
      setCart(cart.map(cartItem => 
        cartItem.id === item.id 
          ? { ...cartItem, quantity: cartItem.quantity + 1 }
          : cartItem
      ));
    } else {
      setCart([...cart, { ...item, quantity: 1 }]);
    }
  };

  const removeFromCart = (itemId) => {
    const existingItem = cart.find(cartItem => cartItem.id === itemId);
    if (existingItem && existingItem.quantity > 1) {
      setCart(cart.map(cartItem => 
        cartItem.id === itemId 
          ? { ...cartItem, quantity: cartItem.quantity - 1 }
          : cartItem
      ));
    } else {
      setCart(cart.filter(cartItem => cartItem.id !== itemId));
    }
  };

  const getTotalPrice = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const getTotalItems = () => {
    return cart.reduce((total, item) => total + item.quantity, 0);
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0
    }).format(price);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-gradient-to-r from-red-600 to-orange-600 text-white shadow-lg">
        <div className="container mx-auto px-4 py-6">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-3xl font-bold">Warung Nusantara</h1>
              <p className="text-red-100 mt-1">Cita Rasa Tradisional Indonesia</p>
            </div>
            <button
              onClick={() => setShowCart(!showCart)}
              className="relative bg-white bg-opacity-20 p-3 rounded-full hover:bg-opacity-30 transition-all"
            >
              <ShoppingCart size={24} />
              {getTotalItems() > 0 && (
                <span className="absolute -top-2 -right-2 bg-yellow-400 text-red-800 rounded-full w-6 h-6 flex items-center justify-center text-sm font-bold">
                  {getTotalItems()}
                </span>
              )}
            </button>
          </div>
        </div>
      </header>

      <div className="container mx-auto px-4 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          {/* Main Content */}
          <div className="flex-1">
            {/* Navigation Tabs */}
            <div className="bg-white rounded-lg shadow-md mb-6">
              <div className="flex border-b">
                {['menu', 'tentang', 'kontak'].map((tab) => (
                  <button
                    key={tab}
                    onClick={() => setActiveTab(tab)}
                    className={`flex-1 py-4 px-6 text-center font-medium capitalize transition-colors ${
                      activeTab === tab
                        ? 'text-red-600 border-b-2 border-red-600 bg-red-50'
                        : 'text-gray-600 hover:text-red-600 hover:bg-gray-50'
                    }`}
                  >
                    {tab === 'tentang' ? 'Tentang Kami' : tab}
                  </button>
                ))}
              </div>
            </div>

            {/* Tab Content */}
            {activeTab === 'menu' && (
              <div>
                {/* Category Filter */}
                <div className="flex flex-wrap gap-2 mb-6">
                  {categories.map((category) => (
                    <button
                      key={category}
                      onClick={() => setSelectedCategory(category)}
                      className={`px-4 py-2 rounded-full transition-colors ${
                        selectedCategory === category
                          ? 'bg-red-600 text-white'
                          : 'bg-white text-gray-600 hover:bg-red-50 hover:text-red-600'
                      }`}
                    >
                      {category}
                    </button>
                  ))}
                </div>

                {/* Menu Grid */}
                <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
                  {filteredMenu.map((item) => (
                    <div key={item.id} className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow">
                      <img
                        src={item.image}
                        alt={item.name}
                        className="w-full h-48 object-cover"
                      />
                      <div className="p-4">
                        <div className="flex justify-between items-start mb-2">
                          <h3 className="text-lg font-semibold text-gray-800">{item.name}</h3>
                          <div className="flex items-center text-yellow-500">
                            <Star size={16} fill="currentColor" />
                            <span className="ml-1 text-sm text-gray-600">{item.rating}</span>
                          </div>
                        </div>
                        <p className="text-gray-600 text-sm mb-3">{item.description}</p>
                        <div className="flex justify-between items-center">
                          <span className="text-xl font-bold text-red-600">
                            {formatPrice(item.price)}
                          </span>
                          <button
                            onClick={() => addToCart(item)}
                            className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-colors flex items-center gap-2"
                          >
                            <Plus size={16} />
                            Tambah
                          </button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {activeTab === 'tentang' && (
              <div className="bg-white rounded-lg shadow-md p-6">
                <h2 className="text-2xl font-bold text-gray-800 mb-4">Tentang Warung Nusantara</h2>
                <div className="space-y-4 text-gray-600">
                  <p>
                    Warung Nusantara adalah restoran yang menghadirkan cita rasa autentik masakan tradisional Indonesia. 
                    Kami berkomitmen untuk menyajikan hidangan berkualitas tinggi dengan resep turun temurun yang telah 
                    diwariskan dari generasi ke generasi.
                  </p>
                  <p>
                    Dengan menggunakan bahan-bahan segar dan bumbu rempah pilihan, setiap hidangan kami dibuat dengan 
                    penuh cinta dan perhatian terhadap detail. Dari Gudeg Yogyakarta hingga Rendang Padang, kami 
                    menghadirkan kelezatan Nusantara di setiap sajian.
                  </p>
                  <div className="bg-red-50 p-4 rounded-lg">
                    <h3 className="font-semibold text-red-800 mb-2">Jam Operasional</h3>
                    <div className="flex items-center text-red-700">
                      <Clock size={16} className="mr-2" />
                      <span>Setiap hari: 08.00 - 22.00 WIB</span>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'kontak' && (
              <div className="bg-white rounded-lg shadow-md p-6">
                <h2 className="text-2xl font-bold text-gray-800 mb-4">Hubungi Kami</h2>
                <div className="space-y-4">
                  <div className="flex items-center text-gray-600">
                    <MapPin size={20} className="mr-3 text-red-600" />
                    <div>
                      <p className="font-medium">Alamat</p>
                      <p>Jl. Raya Nusantara No. 123, Surabaya, Jawa Timur</p>
                    </div>
                  </div>
                  <div className="flex items-center text-gray-600">
                    <Phone size={20} className="mr-3 text-red-600" />
                    <div>
                      <p className="font-medium">Telepon</p>
                      <p>(031) 1234-5678</p>
                    </div>
                  </div>
                  <div className="bg-gradient-to-r from-red-50 to-orange-50 p-4 rounded-lg">
                    <h3 className="font-semibold text-red-800 mb-2">Pesan Antar</h3>
                    <p className="text-gray-700">
                      Nikmati hidangan favorit Anda di rumah! Kami melayani pesan antar dalam radius 5 km 
                      dengan biaya pengiriman mulai dari Rp 5.000.
                    </p>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Cart Sidebar */}
          {showCart && (
            <div className="lg:w-80">
              <div className="bg-white rounded-lg shadow-md p-6 sticky top-4">
                <h3 className="text-xl font-bold text-gray-800 mb-4">Keranjang Pesanan</h3>
                {cart.length === 0 ? (
                  <p className="text-gray-500 text-center py-8">Keranjang masih kosong</p>
                ) : (
                  <>
                    <div className="space-y-3 mb-4 max-h-96 overflow-y-auto">
                      {cart.map((item) => (
                        <div key={item.id} className="flex justify-between items-center bg-gray-50 p-3 rounded-lg">
                          <div className="flex-1">
                            <h4 className="font-medium text-gray-800 text-sm">{item.name}</h4>
                            <p className="text-red-600 font-semibold">{formatPrice(item.price)}</p>
                          </div>
                          <div className="flex items-center gap-2">
                            <button
                              onClick={() => removeFromCart(item.id)}
                              className="bg-red-100 text-red-600 p-1 rounded hover:bg-red-200"
                            >
                              <Minus size={14} />
                            </button>
                            <span className="w-8 text-center font-medium">{item.quantity}</span>
                            <button
                              onClick={() => addToCart(item)}
                              className="bg-red-100 text-red-600 p-1 rounded hover:bg-red-200"
                            >
                              <Plus size={14} />
                            </button>
                          </div>
                        </div>
                      ))}
                    </div>
                    <div className="border-t pt-4">
                      <div className="flex justify-between items-center mb-4">
                        <span className="text-lg font-bold text-gray-800">Total:</span>
                        <span className="text-xl font-bold text-red-600">
                          {formatPrice(getTotalPrice())}
                        </span>
                      </div>
                      <button className="w-full bg-gradient-to-r from-red-600 to-orange-600 text-white py-3 rounded-lg font-semibold hover:from-red-700 hover:to-orange-700 transition-colors">
                        Pesan Sekarang
                      </button>
                    </div>
                  </>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default RestaurantApp;
