import axios from 'axios';

const API_URL = 'http://localhost:8000/api'; // Adjust the URL as needed

// Dashboard API
export const getDashboardData = async () => {
    const response = await axios.get(`${API_URL}/dashboard`);
    return response.data;
};

// Menu API
export const getMenuItems = async () => {
    const response = await axios.get(`${API_URL}/menu`);
    return response.data;
};

export const createMenuItem = async (menuItem) => {
    const response = await axios.post(`${API_URL}/menu`, menuItem);
    return response.data;
};

export const updateMenuItem = async (id, menuItem) => {
    const response = await axios.put(`${API_URL}/menu/${id}`, menuItem);
    return response.data;
};

export const deleteMenuItem = async (id) => {
    const response = await axios.delete(`${API_URL}/menu/${id}`);
    return response.data;
};

// Categories API
export const getCategories = async () => {
    const response = await axios.get(`${API_URL}/categories`);
    return response.data;
};

export const createCategory = async (category) => {
    const response = await axios.post(`${API_URL}/categories`, category);
    return response.data;
};

export const updateCategory = async (id, category) => {
    const response = await axios.put(`${API_URL}/categories/${id}`, category);
    return response.data;
};

export const deleteCategory = async (id) => {
    const response = await axios.delete(`${API_URL}/categories/${id}`);
    return response.data;
};

// Customers API
export const getCustomers = async () => {
    const response = await axios.get(`${API_URL}/customers`);
    return response.data;
};

export const createCustomer = async (customer) => {
    const response = await axios.post(`${API_URL}/customers`, customer);
    return response.data;
};

export const updateCustomer = async (id, customer) => {
    const response = await axios.put(`${API_URL}/customers/${id}`, customer);
    return response.data;
};

export const deleteCustomer = async (id) => {
    const response = await axios.delete(`${API_URL}/customers/${id}`);
    return response.data;
};

// Admin Users API
export const getAdminUsers = async () => {
    const response = await axios.get(`${API_URL}/admin-users`);
    return response.data;
};

export const createAdminUser = async (adminUser) => {
    const response = await axios.post(`${API_URL}/admin-users`, adminUser);
    return response.data;
};

export const updateAdminUser = async (id, adminUser) => {
    const response = await axios.put(`${API_URL}/admin-users/${id}`, adminUser);
    return response.data;
};

export const deleteAdminUser = async (id) => {
    const response = await axios.delete(`${API_URL}/admin-users/${id}`);
    return response.data;
};
