import React, { useEffect, useState } from 'react';
import { getCategories, createCategory, updateCategory, deleteCategory } from '../api/index';
import Categories from '../components/Categories';
import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:8000/api', // Ganti sesuai alamat backend Laravel Anda
  headers: {
    'Content-Type': 'application/json',
  },
});

const CategoriesPage = () => {
    const [categories, setCategories] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const data = await getCategories();
                setCategories(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchCategories();
    }, []);

    const handleCreate = async (category) => {
        try {
            const newCategory = await createCategory(category);
            setCategories([...categories, newCategory]);
        } catch (err) {
            setError(err.message);
        }
    };

    const handleUpdate = async (id, updatedCategory) => {
        try {
            const updated = await updateCategory(id, updatedCategory);
            setCategories(categories.map(cat => (cat.id === id ? updated : cat)));
        } catch (err) {
            setError(err.message);
        }
    };

    const handleDelete = async (id) => {
        try {
            await deleteCategory(id);
            setCategories(categories.filter(cat => cat.id !== id));
        } catch (err) {
            setError(err.message);
        }
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            <h1>Categories</h1>
            <Categories 
                categories={categories} 
                onCreate={handleCreate} 
                onUpdate={handleUpdate} 
                onDelete={handleDelete} 
            />
        </div>
    );
};

export default CategoriesPage;
