import React, { useEffect, useState } from 'react';
import { getCustomers, createCustomer, updateCustomer, deleteCustomer } from '../api/index';
import Customers from '../components/Customers';

const CustomersPage = () => {
    const [customers, setCustomers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchCustomers = async () => {
            try {
                const data = await getCustomers();
                setCustomers(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchCustomers();
    }, []);

    const handleCreate = async (customer) => {
        try {
            const newCustomer = await createCustomer(customer);
            setCustomers([...customers, newCustomer]);
        } catch (err) {
            setError(err.message);
        }
    };

    const handleUpdate = async (id, updatedCustomer) => {
        try {
            const customer = await updateCustomer(id, updatedCustomer);
            setCustomers(customers.map(c => (c.id === id ? customer : c)));
        } catch (err) {
            setError(err.message);
        }
    };

    const handleDelete = async (id) => {
        try {
            await deleteCustomer(id);
            setCustomers(customers.filter(c => c.id !== id));
        } catch (err) {
            setError(err.message);
        }
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            <h1>Customers</h1>
            <Customers 
                customers={customers} 
                onCreate={handleCreate} 
                onUpdate={handleUpdate} 
                onDelete={handleDelete} 
            />
        </div>
    );
};

export default CustomersPage;
